INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('140', '23', '4', '1', 'en_us', '2', '', '{\"typeId\":\"4\",\"authorId\":\"1\",\"title\":\"Troll House\",\"slug\":\"troll-house\",\"postDate\":1419656160,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"67\":{\"new1\":{\"type\":\"hook\",\"enabled\":\"1\",\"fields\":{\"text\":\"Did you know ICG is home to a friendly troll whose home is built with natural methods?\"}},\"new2\":{\"type\":\"mainContent\",\"enabled\":\"1\",\"fields\":{\"text\":\"<p>ICG\'s <em>Troll House<\\/em> is home to a friendly troll, who forages in the hills by day, and returns to his cozy home only at night, after all the boys and girls are nestled in bed.  Our Troll\'s house is actually a straw-bale insulated timber frame structure; a perfect example of natural building styles. Its roof is a green roof, which means it is covered in vegetation with the aim of absorbing rainwater, increasing insulation and creating a habitat.  This structure is a feature on ICG\'s \\\"Green Trail\\\" and is a well-loved hide-out and place for gathering small groups of children. <\\/p>\"}},\"new3\":{\"type\":\"sponsors\",\"enabled\":\"1\",\"fields\":{\"text\":\"<p>Special thanks to Aaron Dennis and ej george of Tugley Wood Timberframing; Lexie Hain and Marguerite Wells of Motherplants Nursery, and all the volunteers who helped build and continue to maintain this magical abode!<\\/p>\"}}},\"72\":\"\"}}', '2015-01-19 22:14:46', '2015-01-19 22:14:46', 'a1d653ce-ba44-4415-a903-2bf3f2577abe');
