INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('149', '223', '4', '1', 'en_us', '1', '', '{\"typeId\":null,\"authorId\":\"1\",\"title\":\"Wildflower Meadow\",\"slug\":\"wildflower-meadow\",\"postDate\":1421720064,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"67\":{\"new1\":{\"type\":\"hook\",\"enabled\":\"1\",\"fields\":{\"text\":\"Do you sometimes like to follow a path to see where it will lead?\"}},\"new2\":{\"type\":\"mainContent\",\"enabled\":\"1\",\"fields\":{\"text\":\"<p>Come into the Wildflower Meadow!  Exit the main path and embark on a journey into a natural meadow full of flowers, rushes, chicory, with statuesque ironweed, and false indigo standing at the entrance during certain times of year.  The Wildflower Meadow connects the Bird Garden, Wetlands, and Cherry Guild at the south end of the Enchanted Edible Forest Garden.  After you visit Gaia, take the secret pathway down through the bioswale, behind the Bird House, and enter the Wildflower Meadow on your way to the Boardwalk. See what kinds of creatures and plants live there!<\\/p>\"}},\"new3\":{\"type\":\"slideshow\",\"enabled\":\"1\",\"fields\":{\"image\":[\"222\"],\"slideshowTitle\":\"\"}},\"new4\":{\"type\":\"sponsors\",\"enabled\":\"1\",\"fields\":{\"text\":\"<p>Planted and developed in 2006-2007 with principle funding from Ithaca Garden Club.<\\/p>\"}}},\"72\":\"\"}}', '2015-01-20 02:14:24', '2015-01-20 02:14:24', '8b4722d5-160e-4657-ad27-7915d6844ce2');
INSERT INTO `craft_entryversions` (`id`, `entryId`, `sectionId`, `creatorId`, `locale`, `num`, `notes`, `data`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('150', '223', '4', '1', 'en_us', '2', '', '{\"typeId\":\"4\",\"authorId\":\"1\",\"title\":\"Wildflower Meadow\",\"slug\":\"wildflower-meadow\",\"postDate\":1421720040,\"expiryDate\":null,\"enabled\":1,\"fields\":{\"67\":{\"224\":{\"type\":\"hook\",\"enabled\":\"1\",\"fields\":{\"text\":\"Do you sometimes like to follow a path to see where it will lead?\"}},\"225\":{\"type\":\"mainContent\",\"enabled\":\"1\",\"fields\":{\"text\":\"<p>\\r\\n\\tCome into the Wildflower Meadow!  Exit the main path and embark on a journey into a natural meadow full of flowers, rushes, chicory, with statuesque ironweed, and false indigo standing at the entrance during certain times of year.  The Wildflower Meadow connects the Bird Garden, Wetlands, and Cherry Guild at the south end of the Enchanted Edible Forest Garden.  After you visit Gaia, take the secret pathway down through the bioswale, behind the Bird House, and enter the Wildflower Meadow on your way to the Boardwalk. See what kinds of creatures and plants live there!\\r\\n<\\/p>\"}},\"226\":{\"type\":\"slideshow\",\"enabled\":\"1\",\"fields\":{\"image\":[\"222\"],\"slideshowTitle\":\"\"}},\"227\":{\"type\":\"sponsors\",\"enabled\":\"1\",\"fields\":{\"text\":\"<p>\\r\\n\\tPlanted and developed in 2006-2007 with principle funding from Ithaca Garden Club.\\r\\n<\\/p>\"}}},\"72\":\"\"}}', '2015-01-20 02:15:22', '2015-01-20 02:15:22', 'f5dca78c-16af-418b-a0f6-49f07addc9a4');
