INSERT INTO `craft_matrixcontent_gardenscontent` (`id`, `elementId`, `locale`, `field_hook_text`, `field_mainContent_text`, `field_slideshow_slideshowTitle`, `field_sponsors_text`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('2', '181', 'en_us', NULL, '<p>\r\n	This unique greenhouse was constructed from hundreds of re-used soda bottles! Its frame is made of locust, a naturally rot-resistant wood and the bottles are held in with bamboo, a renewable building resource. This greenhouse is used for hardening off seedlings when they come out of the greenhouse, and for children to pot up seedlings in spring to take home.  Even though we call it a greenhouse, it is really more of a cold-frame, because it is unheated and not air tight.\r\n</p>', NULL, NULL, '2015-01-19 01:58:55', '2015-01-20 01:51:22', 'd3e0656d-d600-454c-978e-7d554d64e98c');
