INSERT INTO `craft_matrixcontent_gardenscontent` (`id`, `elementId`, `locale`, `field_hook_text`, `field_mainContent_text`, `field_slideshow_slideshowTitle`, `field_sponsors_text`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('20', '225', 'en_us', NULL, '<p>\r\n	Come into the Wildflower Meadow!  Exit the main path and embark on a journey into a natural meadow full of flowers, rushes, chicory, with statuesque ironweed, and false indigo standing at the entrance during certain times of year.  The Wildflower Meadow connects the Bird Garden, Wetlands, and Cherry Guild at the south end of the Enchanted Edible Forest Garden.  After you visit Gaia, take the secret pathway down through the bioswale, behind the Bird House, and enter the Wildflower Meadow on your way to the Boardwalk. See what kinds of creatures and plants live there!\r\n</p>', NULL, NULL, '2015-01-20 02:14:24', '2015-01-20 02:15:22', 'd578e827-0007-40b6-b6f6-4315dbf04ed0');
