INSERT INTO `craft_matrixcontent_programfullcontent` (`id`, `elementId`, `locale`, `field_textBlock_text`, `field_quoteBlock_quote`, `field_imageBlock_caption`, `field_quoteBlock_citation`, `field_quoteBlock_position`, `field_videoBlock_videoTitle`, `field_videoBlock_videoEmbedCode`, `field_videoBlock_position`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('6', '110', 'en_us', '<p>ICG\'s annual Festival of FIRE &amp; ICE brings fun, art, and creativity to the coldest and snowiest time of the year with an outdoor extravaganza for all ages to enjoy. Hands-on activities include fire sculptures, ice sculptures, sledding, hot chocolate, snow fort and den building, the famous ice laboratory, food-coloring snow mound mural - all with bonfires, fire performance, drumming, and hot cocoa in a giant cauldron on site. Yes! Bring your own frozen ice creations and we\'ll add it to the collaborative fun.</p><p>Bring some cash for a hot meal and firey/icey treats, be sure to dress for the weather, and join us at the Ithaca Children\'s Garden Hands-on-Nature Anarcy Zone! 500 people attended in 2013 - come be part of the tradition!</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2015-01-04 16:07:31', '2015-01-04 16:07:31', 'd8748555-7d5c-47c7-b4c4-4b506d013271');
