INSERT INTO `craft_matrixcontent_programfullcontent` (`id`, `elementId`, `locale`, `field_textBlock_text`, `field_quoteBlock_quote`, `field_imageBlock_caption`, `field_quoteBlock_citation`, `field_quoteBlock_position`, `field_videoBlock_videoTitle`, `field_videoBlock_videoEmbedCode`, `field_videoBlock_position`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('8', '117', 'en_us', '<p>Chase your cabin fever away with ICG. Join us for stories of the natural world in winter, and the promise of spring - then dig in to a great hands-on project!</p><p><img src=\"{asset:115:url}\" alt=\"sprouts-junk.png#asset:115\" /><br /></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2015-01-04 22:17:14', '2015-01-10 18:28:49', '3190604f-1604-4525-9e72-bcdb503d921f');
