INSERT INTO `craft_matrixcontent_programfullcontent` (`id`, `elementId`, `locale`, `field_textBlock_text`, `field_quoteBlock_quote`, `field_imageBlock_caption`, `field_quoteBlock_citation`, `field_quoteBlock_position`, `field_videoBlock_videoTitle`, `field_videoBlock_videoEmbedCode`, `field_videoBlock_position`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('10', '127', 'en_us', '<h3>Special Program Notes</h3><ul><li>For grades 1st - 3rd</li><li>M-F, Feb. 16 - 20</li><li>9:00am - 4:00pm; Early Drop-off starts at 8:30am</li><li>Registration Required</li></ul><h3>Program Registration Fee:</h3><ul><li>Self-determined sliding scale = <em>$230-280 / Members $215</em></li><li>Early Bird Registration Deadline: January 15th = <em>$195 / Members $180</em></li><li>Sibling Discount = <em>10% off each additional child registered from same family</em></li><li><em>Scholarships available - conact ICG for more information!</em></li></ul>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2015-01-10 14:52:38', '2015-01-10 18:31:49', '2dab7c5f-d7a5-4a61-ad61-017cbc476b8c');
INSERT INTO `craft_matrixcontent_programfullcontent` (`id`, `elementId`, `locale`, `field_textBlock_text`, `field_quoteBlock_quote`, `field_imageBlock_caption`, `field_quoteBlock_citation`, `field_quoteBlock_position`, `field_videoBlock_videoTitle`, `field_videoBlock_videoEmbedCode`, `field_videoBlock_position`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('11', '131', 'en_us', '<p>Enchanted Spring welcomes children 3 to 5 years old to explore with us at Ithaca Children\'s Garden. Spend your mornings full of imagination, exploration, and curiosity. Enchanted Spring is a fun, play-based, educational program designed to help pre-school age children connect with and develop a love for their natural world. Lessons are nature-focused and child-centered, and take place in ICG\'s vegetable garden, wetland habitat, wildflower meadow, outdoor kitchen, and Hands-On Nature Anarchy Zone. Parents may choose to stay and play, or drop off.</p><h3>Special Program Notes</h3><p>Space is limited, and pre-registration is required.<br />Self-determined sliding scale: $80-100<br />ICG members: $70</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2015-01-10 14:57:54', '2015-01-10 18:24:05', '8261b0c0-eb2f-4655-b364-205aefbdc77c');
INSERT INTO `craft_matrixcontent_singlepagecontent` (`id`, `elementId`, `locale`, `field_textBlock_text`, `field_slideshow_slideshowTitle`, `field_slideshow_position`, `field_quoteBlock_quote`, `field_quoteBlock_citation`, `field_quoteBlock_position`, `field_videoBlock_videoTitle`, `field_videoBlock_videoEmbedCode`, `field_videoBlock_position`, `field_relationsBlock_relatedBlockTitle`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('1', '87', 'en_us', '<p>The Ithaca Children\'s Garden (ICG) is both a place and a program, located on the Cayuga Waterfront Trail, minutes from downtown Ithaca. ICG has spent over a decade now building a three-acre public children\'s garden as well as developing, implementing, and evaluating a wide range of educational programs for children, teens, and the community.</p><p>ICG works in coordination with Cornell Cooperative Extension to bring outstanding educational programming to the residents of Tompkins County and beyond. Our mutual commitment is to accessibility, inclusion, and promoting stewardship of the natural environment through hands-on, garden-based learning.</p><p>The ICG holds educational programs throughout the year with the majority occurring during the growing season, between April and October. During June, July, and August, programs run nearly every Tuesday, Wednesday, Thursday, and Saturday at the Garden. Some of these programs are free, drop-in programs, while others, such as our Salad Farmers program and summer camps, require registration and fees. We provide scholarships for those who would be unable to participate without</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2015-01-03 15:59:38', '2015-01-03 17:55:20', '82b76c9a-c024-43a3-87d0-7c425b9eb539');
